var $ = require('jquery')
  , Backbone = require('backbone')
  , _ = require('underscore')
  , PersonView = require('person/PersonView')

module.exports = Backbone.View.extend({
  tagName: 'ul',
  className: 'pList',
  render: function(){
    var $el = $(this.el)

    this.collection.each(function(list) {
      var item = new PersonView({ model: list });
      $el.append(item.render().el);
    });

    return this;
  }
})
